import React, { useState, useRef } from 'react';
import { XMarkIcon } from '@heroicons/react/24/outline';
import { FaceSmileIcon, FaceFrownIcon, FireIcon, ExclamationCircleIcon, CheckCircleIcon } from '@heroicons/react/24/solid';

interface JournalEntry {
  id: string;
  content: string;
  mood: '开心' | '伤心' | '生气' | '焦虑' | '平静';
  timestamp: Date;
}

interface JournalProps {
  onBack?: () => void;
}

const Journal: React.FC<JournalProps> = ({ onBack }) => {
  const [entries, setEntries] = useState<JournalEntry[]>([]);
  const [content, setContent] = useState('');
  const [mood, setMood] = useState<'开心' | '伤心' | '生气' | '焦虑' | '平静'>('平静');
  const [rotation, setRotation] = useState(0);
  const [selectedIndex, setSelectedIndex] = useState(4); // 默认选中“平静”

  const moods: Array<{ mood: JournalEntry['mood']; icon: JSX.Element }> = [
    { mood: '开心', icon: <FaceSmileIcon className="h-8 w-8 text-yellow-500" /> },
    { mood: '伤心', icon: <FaceFrownIcon className="h-8 w-8 text-blue-500" /> },
    { mood: '生气', icon: <FireIcon className="h-8 w-8 text-red-500" /> },
    { mood: '焦虑', icon: <ExclamationCircleIcon className="h-8 w-8 text-orange-500" /> },
    { mood: '平静', icon: <CheckCircleIcon className="h-8 w-8 text-green-500" /> },
  ];

  const wheelRef = useRef<HTMLDivElement>(null);
  const startAngleRef = useRef<number | null>(null);
  const currentRotationRef = useRef(rotation);

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    const rect = wheelRef.current?.getBoundingClientRect();
    if (!rect) return;

    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const startAngle = Math.atan2(e.clientY - centerY, e.clientX - centerX) * (180 / Math.PI);
    startAngleRef.current = startAngle;
    currentRotationRef.current = rotation;

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleMouseMove = (e: MouseEvent) => {
    if (startAngleRef.current === null) return;

    const rect = wheelRef.current?.getBoundingClientRect();
    if (!rect) return;

    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const currentAngle = Math.atan2(e.clientY - centerY, e.clientX - centerX) * (180 / Math.PI);
    const deltaAngle = currentAngle - startAngleRef.current;

    setRotation(currentRotationRef.current + deltaAngle);
  };

  const handleMouseUp = () => {
    document.removeEventListener('mousemove', handleMouseMove);
    document.removeEventListener('mouseup', handleMouseUp);

    // 计算最终选中的情绪
    const normalizedRotation = (rotation % 360 + 360) % 360; // 将角度归一化到 [0, 360)
    const index = Math.round(normalizedRotation / 72) % 5; // 每个图标占 72 度，正下方的图标为选中位置
    setSelectedIndex(index);
    setMood(moods[index].mood);
  };

  const handleSubmit = () => {
    if (content.trim()) {
      const newEntry: JournalEntry = {
        id: Date.now().toString(),
        content,
        mood,
        timestamp: new Date(),
      };
      setEntries([newEntry, ...entries]);
      setContent('');
      setMood('平静');
      setSelectedIndex(4); // 重置为“平静”
    }
  };

  return (
    <div className="h-full flex flex-col">
      {/* 顶部栏 */}
      <div className="flex items-center justify-between px-4 pt-4 pb-2 bg-transparent">
        <button onClick={onBack} className="p-2 rounded-full hover:bg-gray-100">
          <XMarkIcon className="h-6 w-6 text-gray-500" />
        </button>
        <div className="flex-1 text-center -ml-8">
          <span className="text-base font-semibold text-gray-700">心情日记</span>
        </div>
        <div style={{ width: 40 }} /> {/* 占位 */}
      </div>

      {/* 日记输入区域 */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">记录你的心情</h2>

          {/* 转盘选择心情 */}
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">今天的心情</label>
            <div className="relative flex items-center justify-center">
              {/* 转盘 */}
              <div
                ref={wheelRef}
                className="w-40 h-40 rounded-full border-4 border-gray-300 flex items-center justify-center"
                style={{
                  transform: `rotate(${rotation}deg)`,
                  transition: 'none',
                }}
                onMouseDown={handleMouseDown}
              >
                {moods.map((item, index) => (
                  <div
                    key={item.mood}
                    className={`absolute ${
                      index === selectedIndex
                        ? 'scale-125 border-2 border-primary-500 shadow-lg'
                        : ''
                    }`}
                    style={{
                      transform: `rotate(${index * 72}deg) translate(0, -80px) rotate(-${index * 72}deg)`,
                      transition: 'transform 0.3s, scale 0.3s',
                    }}
                  >
                    {item.icon}
                  </div>
                ))}
              </div>

              {/* 中心指针 */}
              <div className="absolute w-4 h-4 bg-red-500 rounded-full"></div>
            </div>
          </div>

          {/* 日记输入框 */}
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="写下你的想法..."
            className="w-full h-32 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 mb-4"
          />

          {/* 保存按钮 */}
          <button
            onClick={handleSubmit}
            className="w-full bg-primary-500 text-white py-3 rounded-lg hover:bg-primary-600 transition-colors"
          >
            保存日记
          </button>
        </div>

        {/* 日记列表 */}
        <div className="mt-4 space-y-3">
          {entries.map((entry) => (
            <div
              key={entry.id}
              className="bg-white p-3 rounded-lg border border-gray-200"
            >
              <div className="flex items-center mb-2">
                <span className="text-sm text-gray-500">
                  {entry.timestamp.toLocaleDateString()} - {entry.mood}
                </span>
              </div>
              <p className="text-gray-800">{entry.content}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Journal;
