import React, { useState } from 'react';
import { XMarkIcon } from '@heroicons/react/24/outline';

interface JournalEntry {
  id: string;
  content: string;
  mood: 'very-happy' | 'happy' | 'neutral' | 'sad' | 'very-sad';
  timestamp: Date;
}

interface JournalProps {
  onBack?: () => void;
}

const Journal: React.FC<JournalProps> = ({ onBack }) => {
  const [entries, setEntries] = useState<JournalEntry[]>([]);
  const [content, setContent] = useState('');
  const [mood, setMood] = useState<'very-happy' | 'happy' | 'neutral' | 'sad' | 'very-sad'>('neutral');
  const [rotation, setRotation] = useState(0);
  const [isSpinning, setIsSpinning] = useState(false);

  // 心情转盘配置
  const moods = [
    { value: 'very-happy', icon: '😄', color: 'bg-yellow-400' },
    { value: 'happy', icon: '🙂', color: 'bg-yellow-300' },
    { value: 'neutral', icon: '😐', color: 'bg-gray-300' },
    { value: 'sad', icon: '🙁', color: 'bg-blue-300' },
    { value: 'very-sad', icon: '😢', color: 'bg-blue-400' }
  ];

  const spinWheel = () => {
    if (isSpinning) return;
    
    setIsSpinning(true);
    // 随机旋转5-10圈
    const spins = 5 + Math.floor(Math.random() * 5);
    const degrees = spins * 360 + Math.floor(Math.random() * 360);
    
    setRotation(degrees);
    
    // 计算最终停止位置对应的心情
    setTimeout(() => {
      const finalRotation = degrees % 360;
      const moodIndex = Math.floor((360 - finalRotation) / (360 / moods.length));
      setMood(moods[moodIndex].value);
      setIsSpinning(false);
    }, 3000); // 3秒后停止
  };

  const handleSubmit = () => {
    if (content.trim()) {
      const newEntry: JournalEntry = {
        id: Date.now().toString(),
        content,
        mood,
        timestamp: new Date(),
      };
      setEntries([newEntry, ...entries]);
      setContent('');
      setMood('neutral');
    }
  };

  return (
    <div className="h-full flex flex-col">
      {/* 顶部栏 */}
      <div className="flex items-center justify-between px-4 pt-4 pb-2 bg-transparent">
        <button onClick={onBack} className="p-2 rounded-full hover:bg-gray-100">
          <XMarkIcon className="h-6 w-6 text-gray-500" />
        </button>
        <div className="flex-1 text-center -ml-8">
          <span className="text-base font-semibold text-gray-700">心情日记</span>
        </div>
        <div style={{ width: 40 }} /> {/* 占位 */}
      </div>

      {/* 日记输入区域 */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="bg-white rounded-lg shadow-lg p-4">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">记录你的心情</h2>

          {/* 心情选择转盘 */}
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">今天的心情</label>
            <div className="relative w-64 h-64 mx-auto">
              {/* 转盘背景 */}
              <div 
                className="absolute w-full h-full rounded-full border-4 border-gray-200"
                style={{
                  transform: `rotate(${rotation}deg)`,
                  transition: isSpinning ? 'transform 3s cubic-bezier(0.2, 0.8, 0.2, 1)' : 'none'
                }}
              >
                {moods.map((moodItem, index) => {
                  const angle = (360 / moods.length) * index;
                  return (
                    <div
                      key={moodItem.value}
                      className={`absolute w-1/2 h-1/2 origin-bottom-right ${moodItem.color}`}
                      style={{
                        transform: `rotate(${angle}deg)`,
                        clipPath: 'polygon(0 0, 100% 0, 100% 100%)'
                      }}
                    >
                      <div
                        className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"
                        style={{
                          transform: `rotate(${-angle}deg) translateY(-50%)`
                        }}
                      >
                        <span className="text-2xl">{moodItem.icon}</span>
                      </div>
                    </div>
                  );
                })}
              </div>
              
              {/* 指针 */}
              <div className="absolute top-0 left-1/2 transform -translate-x-1/2 w-4 h-8 bg-red-500 clip-triangle" />
              
              {/* 旋转按钮 */}
              <button
                onClick={spinWheel}
                disabled={isSpinning}
                className={`absolute bottom-0 left-1/2 transform -translate-x-1/2 px-4 py-2 rounded-full bg-primary-500 text-white ${
                  isSpinning ? 'opacity-50 cursor-not-allowed' : 'hover:bg-primary-600'
                }`}
              >
                {isSpinning ? '旋转中...' : '旋转选择'}
              </button>
            </div>
          </div>

          {/* 日记输入框 */}
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="写下你的想法..."
            className="w-full h-32 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 mb-4"
          />

          {/* 保存按钮 */}
          <button
            onClick={handleSubmit}
            className="w-full bg-primary-500 text-white py-3 rounded-lg hover:bg-primary-600 transition-colors"
          >
            保存日记
          </button>
        </div>

        {/* 日记列表 */}
        <div className="mt-4 space-y-3">
          {entries.map((entry) => (
            <div
              key={entry.id}
              className="bg-white p-3 rounded-lg border border-gray-200"
            >
              <div className="flex items-center mb-2">
                <span className="text-2xl mr-2">
                  {moods.find(m => m.value === entry.mood)?.icon}
                </span>
                <span className="text-sm text-gray-500">
                  {entry.timestamp.toLocaleDateString()}
                </span>
              </div>
              <p className="text-gray-800">{entry.content}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Journal;
