import React, { useState } from 'react';
import { FaceSmileIcon, FaceFrownIcon, FaceMehIcon } from '@heroicons/react/24/outline';

interface JournalEntry {
  id: string;
  content: string;
  mood: 'happy' | 'neutral' | 'sad';
  timestamp: Date;
}

const Journal: React.FC = () => {
  const [entries, setEntries] = useState<JournalEntry[]>([]);
  const [content, setContent] = useState('');
  const [mood, setMood] = useState<'happy' | 'neutral' | 'sad'>('neutral');

  const handleSubmit = () => {
    if (content.trim()) {
      const newEntry: JournalEntry = {
        id: Date.now().toString(),
        content,
        mood,
        timestamp: new Date(),
      };
      setEntries([newEntry, ...entries]);
      setContent('');
      setMood('neutral');
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-2xl font-semibold mb-6 text-gray-800">记录你的心情</h2>
        
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">今天的心情</label>
          <div className="flex space-x-4">
            <button
              onClick={() => setMood('happy')}
              className={`p-2 rounded-full ${
                mood === 'happy' ? 'bg-yellow-100 text-yellow-600' : 'text-gray-400'
              }`}
            >
              <FaceSmileIcon className="h-6 w-6" />
            </button>
            <button
              onClick={() => setMood('neutral')}
              className={`p-2 rounded-full ${
                mood === 'neutral' ? 'bg-gray-100 text-gray-600' : 'text-gray-400'
              }`}
            >
              <FaceMehIcon className="h-6 w-6" />
            </button>
            <button
              onClick={() => setMood('sad')}
              className={`p-2 rounded-full ${
                mood === 'sad' ? 'bg-blue-100 text-blue-600' : 'text-gray-400'
              }`}
            >
              <FaceFrownIcon className="h-6 w-6" />
            </button>
          </div>
        </div>

        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="写下你的想法..."
          className="w-full h-40 p-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 mb-4"
        />

        <button
          onClick={handleSubmit}
          className="w-full bg-primary-500 text-white py-3 rounded-lg hover:bg-primary-600 transition-colors"
        >
          保存日记
        </button>

        <div className="mt-8 space-y-4">
          {entries.map((entry) => (
            <div
              key={entry.id}
              className="bg-gray-50 p-4 rounded-lg border border-gray-200"
            >
              <div className="flex items-center mb-2">
                {entry.mood === 'happy' && (
                  <FaceSmileIcon className="h-5 w-5 text-yellow-500 mr-2" />
                )}
                {entry.mood === 'neutral' && (
                  <FaceMehIcon className="h-5 w-5 text-gray-500 mr-2" />
                )}
                {entry.mood === 'sad' && (
                  <FaceFrownIcon className="h-5 w-5 text-blue-500 mr-2" />
                )}
                <span className="text-sm text-gray-500">
                  {entry.timestamp.toLocaleDateString()}
                </span>
              </div>
              <p className="text-gray-800">{entry.content}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Journal;
